import React, { useState } from "react";
import Ticket from "./Ticket";
import { useDrop } from "react-dnd";
import "../Column.css";
import { itemTypes } from "../types";
import { TicketContext } from "./KanbanTable";

export default function Column(props) {
  const [inputText, setInputText] = useState("");
  const tickets = props.allTickets.filter(
    (ticket) => ticket.category === props.category
  );

  const [{ isOver }, drop] = useDrop({
    accept: itemTypes.TICKET,
    drop: (item) => props.moveTicket(item.text, props.category),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  });

  const addCard = () => {};

  const handleChange = (event) => {
    setInputText(event.target.value);
  };

  return (
    <div className="container">
      <h3 className="header">{props.header}</h3>
      <div
        ref={drop}
        className="column"
        style={{ backgroundColor: isOver ? "lightgray" : "white" }}
      >
        {tickets.map((ticket) => {
          return <Ticket text={ticket.text} />;
        })}
        <div>
          <input onChange={handleChange} value={inputText} />
          <button onClick={addCard}>add</button>
        </div>
      </div>
    </div>
  );
}
