import React, { useState } from "react";
import Ticket from "./Ticket";
import { useDrop } from "react-dnd";
import "../Column.css";
import { itemTypes } from "../types";

export default function Column(props) {
  const [inputText, setInputText] = useState("");
  const tickets = props.allTickets.filter(
    (ticket) => ticket.category === props.category
  );
  // hook for the react-dnd library
  // makes this component able to recieve dragged items
  const [{ isOver }, drop] = useDrop({
    accept: itemTypes.TICKET,
    drop: (item) => props.moveTicket(item.text, props.category),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  });

  const addTicket = () => {
    console.log(inputText, props.category);
    props.addTicket({ text: inputText, category: props.category });
  };

  const handleChange = (event) => {
    setInputText(event.target.value);
  };

  return (
    <div className="container">
      <h3 className="cHeader">{props.header}</h3>
      <div
        ref={drop}
        className="column"
        style={{ backgroundColor: isOver ? "lightgray" : "white" }}
      >
        {tickets.map((ticket) => {
          return <Ticket text={ticket.text} />;
        })}
        <div>
          <input onChange={handleChange} value={inputText} />
          <button onClick={addTicket}>add</button>
        </div>
      </div>
    </div>
  );
}
