import React, { useState } from "react";
import Column from "./Column";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";

import "../Kanban.css";

export default function KanbanTable() {
  const [tickets, setTickets] = useState([
    { text: "Mee töihin", category: "todo" },
    { text: "tee sitä", category: "todo" },
    { text: "tee tätä", category: "todo" },
    { text: "tee hommia", category: "ip" },
    { text: "tämmöstä", category: "done" },
  ]);

  const moveTicket = (text, category) => {
    tickets.forEach((ticket) => {
      if (ticket.text === text) {
        ticket.category = category;
      }
    });
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="container">
        <h1 className="header">Kanban Table</h1>
        <div className="kanban">
          <Column
            header="ToDo"
            allTickets={tickets}
            moveTicket={moveTicket}
            category="todo"
          />
          <Column
            header="In Progress"
            allTickets={tickets}
            moveTicket={moveTicket}
            category="ip"
          />
          <Column
            header="Done"
            allTickets={tickets}
            moveTicket={moveTicket}
            category="done"
          />
        </div>
      </div>
    </DndProvider>
  );
}
