import React from "react";
import { useDrag } from "react-dnd";
import "../Ticket.css";

export default function Ticket({ text }) {
  const [{ isDragging }, drag] = useDrag({
    item: { type: "ticket", text: text },
  });

  return (
    <div
      ref={drag}
      style={{ opacity: isDragging ? 0.5 : 1 }}
      className="ticket"
    >
      <p>{text}</p>
    </div>
  );
}
