import React from "react";
import { useDrag } from "react-dnd";
import "../Ticket.css";

export default function Ticket({ text }) {
  const [{ isDragging }, drag] = useDrag({
    item: { type: "ticket" },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  return (
    <div ref={drag} className="ticket">
      <p>{text}</p>
    </div>
  );
}
